  //               enteredAmount: expense.amount,
  //               enteredDate: expense.date
  //             };
  //   });
};

const App = () => {
  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} />
      <Expenses item={expenses}></Expenses>
    </div>
  );
};

export default App;
